template.value:
createFromCoder(coder: DataCoder): $TYPE$ {
    const obj = new $TYPE$();
    obj.decodeProperties(coder);
    return obj;
}

readonly typeName: string = '$NAME$';

encodeProperties(coder: DataCoder) {
    super.encodeProperties(coder);
    $END$
}

decodeProperties(coder: DataCoder) {
    super.decodeProperties(coder);
}
